	SELECT
	*
	FROM
		bio_order mo
	
		1=1 
		
		
		
			AND mo.CLASSIFY = #{classify}
				
		
			AND mo.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
		
		
			AND mo.CAGE_NO = #{cageno,jdbcType=VARCHAR}
		
		
			AND mo.IS_FINISH = #{isFinish,jdbcType=VARCHAR}
		
		
			AND mo.IS_PROBLEM = #{isProblem,jdbcType=VARCHAR}
		
		
			AND mo.ORDER_STATUS = #{orderStatus,jdbcType=VARCHAR}
				
		
       		AND mo.CREATED_AT BETWEEN date_format(#{startDate},'%Y-%m-%d') AND date_format(#{endDate},'%Y-%m-%d')
	    
	    
			AND mo.CITY = #{city,jdbcType=VARCHAR}
		
		
			AND mo.AREA = #{area,jdbcType=VARCHAR}
		
		
			AND mo.SENDER_ID = #{senderid,jdbcType=VARCHAR} OR mo.RECIVER_ID = #{reciverid,jdbcType=VARCHAR}
		
		
			AND mo.TRANSPORT_ID = #{transportid,jdbcType=VARCHAR}
		
	 
	ORDER BY mo.ID DESC
	limit #{from},#{pageSize}

