	SELECT
		*
	FROM bio_order mo
	
		1=1 
		
		
		
       		AND mo.CREATED_AT BETWEEN date_format(#{startDate},'%Y-%m-%d') AND date_format(#{endDate},'%Y-%m-%d')
	    
		
			AND mo.IS_PROBLEM = #{isProblem,jdbcType=VARCHAR}
		
		
			AND mo.ORDER_STATUS = #{orderStatus,jdbcType=VARCHAR}
		
		
			AND mo.CITY LIKE CONCAT('%',#{city,jdbcType=VARCHAR},'%' )
		
		
			AND mo.AREA LIKE CONCAT('%',#{area,jdbcType=VARCHAR},'%' )
		
		
			AND mo.SENDER_ID = #{senderid,jdbcType=VARCHAR}
		
		
			AND mo.TRANSPORT_ID = #{transportid,jdbcType=VARCHAR}
		
		
			AND mo.SENDER LIKE CONCAT('%',#{sender,jdbcType=VARCHAR},'%' )
		
		
			AND mo.TRANSPORT LIKE CONCAT('%',#{transport,jdbcType=VARCHAR},'%' )
		
	
	ORDER BY mo.ORDER_STATUS DESC
	limit #{from},#{pageSize}

SELECT count(*) FROM bio_order mo
1=1
AND mo.CREATED_AT BETWEEN date_format(#{startDate},'%Y-%m-%d') AND date_format(#{endDate},'%Y-%m-%d')
AND mo.IS_PROBLEM = #{isProblem,jdbcType=VARCHAR}
AND mo.ORDER_STATUS = #{orderStatus,jdbcType=VARCHAR}
AND mo.CITY LIKE CONCAT('%',#{city,jdbcType=VARCHAR},'%' )
AND mo.AREA LIKE CONCAT('%',#{area,jdbcType=VARCHAR},'%' )
AND mo.SENDER_ID = #{senderid,jdbcType=VARCHAR}
AND mo.TRANSPORT_ID = #{transportid,jdbcType=VARCHAR}
AND mo.SENDER LIKE CONCAT('%',#{sender,jdbcType=VARCHAR},'%' )
AND mo.TRANSPORT LIKE CONCAT('%',#{transport,jdbcType=VARCHAR},'%' )
ORDER BY mo.ORDER_STATUS DESC

