SELECT COUNT(e.id) FROM ( SELECT NOW() AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 1 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 2 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 3 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 4 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 5 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 6 DAY) AS DATES )a LEFT JOIN bio_order e ON DATE_FORMAT(CREATED_AT,'%Y-%m-%d') = DATE_FORMAT(a.DATES,'%Y-%m-%d')
AND CITY LIKE CONCAT('%',#{city,jdbcType=VARCHAR},'%' )
AND AREA LIKE CONCAT('%',#{area,jdbcType=VARCHAR},'%' )
AND SENDER_ID = #{senderid,jdbcType=VARCHAR}
AND TRANSPORT_ID = #{transportid,jdbcType=VARCHAR}
GROUP BY DATE_FORMAT(a.DATES,'%Y-%m-%d') ORDER BY a.DATES

SELECT COUNT(e.id) FROM ( SELECT NOW() AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 1 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 2 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 3 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 4 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 5 DAY) AS DATES UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 6 DAY) AS DATES )a LEFT JOIN bio_order e ON DATE_FORMAT(CREATED_AT,'%Y-%m-%d') = DATE_FORMAT(a.DATES,'%Y-%m-%d') AND IS_PROBLEM = 1 AND ORDER_STATUS = 4
AND CITY LIKE CONCAT('%',#{city,jdbcType=VARCHAR},'%' )
AND AREA LIKE CONCAT('%',#{area,jdbcType=VARCHAR},'%' )
AND SENDER_ID = #{senderid,jdbcType=VARCHAR}
AND TRANSPORT_ID = #{transportid,jdbcType=VARCHAR}
GROUP BY DATE_FORMAT(a.DATES,'%Y-%m-%d') ORDER BY a.DATES
