SELECT mp.ID, mp.ORDER_NO, mp.STATUS, mp.DISCRIBTION, mp.CREATED_AT, mp.SENDER_DISCRIBTION, mp.TRANSPORT_DISCRIBTION, mp.SENDER_BY, mp.SENDERAT_AT, mp.TRANSPORT_BY, mp.TRANSPORT_AT FROM bio_problemmonitor mp LEFT JOIN bio_order mo ON mo.ORDER_NO = mp.ORDER_NO
1=1
AND mp.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
AND mp.STATUS = #{status,jdbcType=VARCHAR}
AND mo.SENDER_ID = #{senderid}
AND mo.TRANSPORT_ID = #{transportid}
AND mo.ORDER_STATUS = 3 AND mp.STATUS = 0
limit #{from},#{pageSize}

